-- Create Table with Identity --
CREATE TABLE dbo.T2
(
    C1 INT IDENTITY(1,1),
    C2 VARCHAR(30)
)
WITH
(
    DISTRIBUTION = HASH(C2),
    CLUSTERED COLUMNSTORE INDEX
);


-- Use Insert ... select to populate the table from an  table

INSERT INTO dbo.T2
(C2)

SELECT C2
FROM dbo.T1;

SELECT * 
FROM dbo.T2;

DBCC PDW_SHOWSPACEUSED('dbo.T2');

